mapper.update:
UPDATE file
SET is_deleted = true, modified_at=now()
WHERE file_id=#{fileId} and is_deleted IS NULL;

mapper.select:
SELECT f.file_id, f.size, f.path, f.created_at, f.file_name, f.is_deleted
FROM file_info fi
JOIN file f ON fi.file_id = f.file_id
WHERE fi.table_name = #{tableName} AND fi.table_key = #{tableKey}
AND f.is_deleted IS NULL
ORDER BY f.file_id DESC;

mapper.update:
UPDATE file f SET f.is_deleted = true
JOIN file_info fi ON f.file_id = fi.file_id
WHERE fi.table_name = #{tableName} AND fi.table_key = #{tableKey}
