mapper.update:
update log
set content=#{logPatchDto.content},
    log_code=#{logPatchDto.logCode},
    url=#{logPatchDto.url},
    modified_at=#{logPatchDto.modifiedAt}
where log_id = #{logPatchDto.logId}

update log
set is_deleted= true,
    modified_at=#{modifiedAt}
where log_id = #{logId}

mapper.select:
select log_id, content, code, created_at, modified_at, url
from log

    project_id=#{condition.projectId} and is_deleted = false

        AND log_code = #{logCode}


        AND created_at >= #{condition.startDate}


        AND created_at <= #{condition.endDate}


        AND content like concat('%',#{condition.content},'%')

select count(*)
from log
where project_id = #{projectId}
  and is_deleted = false;
